        {{
            "问题类型": "多文档对比问题",
            "共同点": "营业收入",
            "问题": "比较2021年星空航空公司和2021年银河航空公司的营业收入，哪家公司的营业收入更高？"
            "答案": "2021年星空航空公司营业收入更高。"
        }},
        {{
            "问题类型": "多文档对比问题",
            "共同点": "资产收购",
            "问题": "比较2021年Apple公司和2020年Google公司分别进行的资产收购，哪家公司的资产收购金额更大？"
            "答案": "2021年Apple公司的资产收购金额更大。"
        }},
        {{
            "问题类型": "多文档时间序列问题",
            "共同点": "资产收购",
            "问题": "比较2021年Apple公司和2020年Google公司分别进行的资产收购，哪家公司的资产收购时间更早？"
            "答案": "2020年Google公司的资产收购时间更早。"
        }}
    ]

